mapper.select:
	SELECT * FROM (
	    SELECT tb.*, rownum rNum from (
	        SELECT * FROM diary	
	         WHERE userId = #{userId}
        	ORDER BY diaryIdx DESC
	    ) Tb
	)
	where rNum>=#{start}
			and rNum<=#{end}

mapper.update:
UPDATE diary SET
ofileName = #{ofileName, jdbcType=VARCHAR}, sfile=#{sfile, jdbcType=VARCHAR},
description=#{description}, temperature=#{temperature}, 
humidity=#{humidity}, sunlight=#{sunlight}, plantidx=#{plantidx, javaType=java.lang.Long, jdbcType=NUMERIC}
WHERE diaryIdx=#{diaryIdx}

mapper.select:
SELECT diaryIdx, sfile, postdate FROM diary
WHERE TO_CHAR(postdate, 'YYYY') = #{year}
  AND TO_CHAR(postdate, 'MM') = #{month}
  AND userId=#{userId}
ORDER BY postdate DESC
